import React, { useState } from "react";

import Expenses from "./components/Expenses/Expenses";
import NewExpenses from "./components/NewExpenses/NewExpenses";

const expensesArray = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 6, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

const App = () => {
  const [expenses, setExpenses] = useState(expensesArray);

  const expenseHandler = (expensesData) => {
    setExpenses((prevState) => {
      return [expensesData, ...expenses];
    });
  };

  return (
    <div>
      <NewExpenses onAddExpense={expenseHandler} />
      <Expenses items={expenses} />
    </div>
  );
};

export default App;
