        </div>
        <div className="form-element">
          <label htmlFor="date-title">Date</label>
          <input type="date" min="2019-12-01" max="2022-12-01" />
        </div>
        <div className="form-element button">
          <button type="submit" className="btn">
            Add Expense
          </button>
        </div>
      </div>
    </form>
  );
};

export default InputForm;
